{"changed":false,"filter":false,"title":"pins_controller.rb","tooltip":"/app/controllers/pins_controller.rb","value":"class PinsController < ApplicationController\n  before_action :set_pin, only: [:show, :edit, :update, :destroy]\n\n  respond_to :html\n\n  def index\n    @pins = Pin.all\n    respond_with(@pins)\n  end\n\n  def show\n    respond_with(@pin)\n  end\n\n  def new\n    @pin = Pin.new\n    respond_with(@pin)\n  end\n\n  def edit\n  end\n\n  def create\n    @pin = Pin.new(pin_params)\n    @pin.save\n    respond_with(@pin)\n  end\n\n  def update\n    @pin.update(pin_params)\n    respond_with(@pin)\n  end\n\n  def destroy\n    @pin.destroy\n    respond_with(@pin)\n  end\n\n  private\n    def set_pin\n      @pin = Pin.find(params[:id])\n    end\n\n    def pin_params\n      params.require(:pin).permit(:description)\n    end\nend\n","undoManager":{"mark":-1,"position":-1,"stack":[]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":0,"column":0},"end":{"row":0,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1428196089014}